<template>  <q-drawer      v-model="leftDrawerOpen"      show-if-above      bordered      class="bg-white"      :width="280"  >    <q-scroll-area class="fit">      <q-list padding class="text-grey-8">        <q-item class="GNL__drawer-item" v-ripple v-for="link in links1" :key="link.text" clickable>          <q-item-section avatar>            <q-icon :name="link.icon"/>          </q-item-section>          <q-item-section>            <router-link class="sidebar-links" :to="link.path">              <q-item-label>                {{ link.text }}              </q-item-label>            </router-link>          </q-item-section>        </q-item>        <q-separator inset class="q-my-sm"/>        <q-item class="GNL__drawer-item" v-ripple v-for="link in links2" :key="link.text" clickable>          <q-item-section>            <router-link class="sidebar-links" :to="link.path">              <q-item-label>{{ link.text }}                <q-icon v-if="link.icon" :name="link.icon"/>              </q-item-label>            </router-link>          </q-item-section>        </q-item>        <q-item class="GNL__drawer-item" v-ripple clickable>          <q-item-section>            <q-item-label>Logout</q-item-label>          </q-item-section>        </q-item>      </q-list>    </q-scroll-area>  </q-drawer></template><script>import { fasImages, fasNewspaper } from "@quasar/extras/fontawesome-v6";export default {  name: 'SidebarComponent',  computed: {    leftDrawerOpen() {      return this.$store.state.layout.leftDrawerOpen    },  },  setup() {    return {      links1: [        {icon: fasNewspaper, text: 'News', path: '/news'},        {icon: fasImages, text: 'Gallery', path: '/gallery'},        {icon: 'work', text: 'Careers', path: '/careers'},      ],      links2: [        {icon: '', text: 'Settings', path: '/settings'},      ],    }  }}</script><style lang="scss">.sidebar-links {  text-decoration: none;}</style>