<template>  <div>    <div class="row q-py-md q-px-md justify-between">      <h5 class="q-ma-xs">        Gallery      </h5>      <q-btn color="primary" size="md" label="Add new" icon="add" @click="create"/>    </div>    <q-separator inset class="q-my-sm"/>    <div class="row">      <div class="col-lg-3 col-md-4 col-sm-12 col-12 q-pa-sm" v-for="item of gallery" v-bind:key="item.id">        <q-card class="my-card">          <q-img :src="imagePath(item.imageUrl)"></q-img>          <q-card-actions align="between" class="q-py-xs q-px-sm">            <div class="text text-bold">{{ item.title.en }}</div>            <q-btn icon="edit" flat round @click="edit(item.id)"></q-btn>          </q-card-actions>        </q-card>      </div>    </div>  </div></template><script>export default {  name: "GalleryIndex",  data() {    return {      gallery: [{}, {}]    }  },  async created() {    this.gallery = (await this.$http.get('http://localhost:3000/gallery')).data  },  methods: {    create() {      this.$router.push({name: 'gallery_form'})    },    edit(id) {      this.$router.push({name: 'gallery_form', params: {id}})    },    imagePath(path) {      return `${process.env.VUE_APP_API_URL}/${path}`    },  }}</script><style scoped></style>